def add_ingredients():
    for ingredient in INGREDIENTS:
        new_i = Ingredient(name=ingredient)
        db.session.add(new_i)
    db.session.commit()

def add_recipe_ingredients():
    for r_i in RECIPE_INGREDIENTS:
        new_r = Recipe(name=r_i['recipe'])
        db.session.add(new_r)
        db.session.commit()

    for r_i in RECIPE_INGREDIENTS:
        recipe = Recipe.query.filter_by(name=r_i['recipe']).first()
        for item in r_i['ingredients']:
            ingredient = Ingredient.query.filter_by(name=item).first()
            new_r_i = recipe_ingredient.insert().values(recipe_id=recipe.id, ingredient_id=ingredient.id, quantity=r_i['ingredients'][item])
            db.session.execute(new_r_i)
            db.session.commit()
